template:
import React from 'react';

interface NavGroupProps {
  label: string;
  icon?: React.ReactNode;
  children?: React.ReactNode;
}

export function NavGroup({ label, icon, children }: NavGroupProps) {
  return (
    <div style={{ marginBottom: '8px' }}>
      <div
        style={{
          display: 'flex',
          alignItems: 'center',
          gap: '8px',
          padding: '6px 8px',
          fontSize: '{{token:typography.scale.xs}}',
          fontWeight: '{{token:typography.weight.semibold}}',
          color: '{{token:colors.text.muted}}',
          textTransform: 'uppercase',
          letterSpacing: '0.08em',
          fontFamily: '{{token:typography.fontFamily.body}}',
        }}
      >
        {icon && <span>{icon}</span>}
        <span>{label}</span>
      </div>
      <div style={{ display: 'flex', flexDirection: 'column', gap: '2px' }}>
        {children}
      </div>
    </div>
  );
}

interface NavItemProps {
  label: string;
  icon?: React.ReactNode;
  active?: boolean;
  onClick?: () => void;
  href?: string;
}

export function NavItem({ label, icon, active = false, onClick, href }: NavItemProps) {
  const style: React.CSSProperties = {
    display: 'flex',
    alignItems: 'center',
    gap: '10px',
    padding: '8px 12px',
    borderRadius: '8px',
    fontSize: '{{token:typography.scale.sm}}',
    fontWeight: active ? '{{token:typography.weight.medium}}' : '{{token:typography.weight.regular}}',
    color: active ? '{{token:colors.text.primary}}' : '{{token:colors.text.secondary}}',
    background: active ? '{{token:colors.glass.tint}}' : 'transparent',
    backdropFilter: active ? '{{token:blur.elevation.1}}' : 'none',
    transition: '{{token:animation.transition.default}}',
    cursor: 'pointer',
    textDecoration: 'none',
    border: 'none',
    width: '100%',
    textAlign: 'left',
    fontFamily: '{{token:typography.fontFamily.body}}',
  };

  if (href) {
    return <a href={href} style={style}>{icon && <span>{icon}</span>}<span>{label}</span></a>;
  }
  return <button onClick={onClick} style={style}>{icon && <span>{icon}</span>}<span>{label}</span></button>;
}
