template:
import React from 'react';
import { NavGroup } from '@/components/preset';

interface SidebarProps {
  collapsed?: boolean;
  onToggle?: () => void;
  children?: React.ReactNode;
}

export function Sidebar({ collapsed = false, onToggle, children }: SidebarProps) {
  return (
    <aside
      style={{
        width: collapsed ? '{{token:spacing.sidebar.collapsedWidth}}' : '{{token:spacing.sidebar.width}}',
        transition: '{{token:animation.transition.sidebar}}',
        backdropFilter: '{{token:blur.elevation.2}}',
        background: '{{token:colors.base.surface}}',
        borderRight: `1px solid {{token:colors.base.border}}`,
        padding: '{{token:spacing.sidebar.padding}}',
        height: '100vh',
        display: 'flex',
        flexDirection: 'column',
        overflow: 'hidden',
      }}
    >
      {children}
    </aside>
  );
}

cssModule:
.sidebar {
