import React from 'react';

interface GlassCardProps {
  children?: React.ReactNode;
  className?: string;
  style?: React.CSSProperties;
}

export function GlassCard({ children, className, style }: GlassCardProps) {
  return (
    <div
      className={className}
      style={{
        backdropFilter: '{{token:blur.elevation.1}}',
        background: '{{token:colors.glass.tint}}',
        border: `1px solid {{token:colors.glass.highlight}}`,
        borderRadius: '{{token:spacing.card.borderRadius}}',
        padding: '{{token:spacing.card.padding}}',
        boxShadow: `0 8px 32px {{token:colors.glass.shadow}}`,
        transition: '{{token:animation.transition.glass}}',
        ...style,
      }}
    >
      {children}
    </div>
  );
}

