template:
import React from 'react';
import { Sidebar } from '@/components/preset';

interface DashboardLayoutProps {
  sidebar?: React.ReactNode;
  topbar?: React.ReactNode;
  children?: React.ReactNode;
}

export function DashboardLayout({ sidebar, topbar, children }: DashboardLayoutProps) {
  const [collapsed, setCollapsed] = React.useState(false);

  return (
    <div
      style={{
        display: 'flex',
        height: '100vh',
        overflow: 'hidden',
        background: '{{token:colors.base.bg}}',
        fontFamily: '{{token:typography.fontFamily.body}}',
      }}
    >
      <Sidebar collapsed={collapsed} onToggle={() => setCollapsed((v) => !v)}>
        {sidebar}
      </Sidebar>
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column', overflow: 'hidden' }}>
        {topbar && (
          <header
            style={{
              backdropFilter: '{{token:blur.elevation.1}}',
              background: '{{token:colors.base.surface}}',
              borderBottom: `1px solid {{token:colors.base.border}}`,
              padding: '0 24px',
              height: '56px',
              display: 'flex',
              alignItems: 'center',
              flexShrink: 0,
            }}
          >
            {topbar}
          </header>
        )}
        <main
          style={{
            flex: 1,
            overflow: 'auto',
            padding: '{{token:spacing.card.padding}}',
          }}
        >
          {children}
        </main>
      </div>
    </div>
  );
}
